mapper.update:
UPDATE tb_file_user
SET file_dir_name = #{fileDirName}, file_name = #{fileName}, file_usr_cret_date = SYSDATE()
WHERE usr_seq = #{usrSeq}

mapper.select:
SELECT COUNT(*)
FROM tb_file_user
WHERE usr_seq = #{usrSeq}

SELECT file_dir_name, file_name
FROM tb_file_user
WHERE usr_seq = #{usrSeq}
